mapper.select:
SELECT id, category_id, category_name, shop_id, shop_entity_id, parent_category_id, pic_url, sort, position_id, is_del, create_time, c_time_stamp FROM goods_category WHERE is_del='N'
AND shop_id=#{shopId}
AND shop_entity_id=#{shopEntityId}
GROUP BY category_id

SELECT id, category_id, category_name, shop_id, shop_entity_id, parent_category_id, pic_url, sort, position_id, is_del, create_time, c_time_stamp FROM goods_category WHERE is_del='N' AND shop_id=#{shopId}
AND shop_entity_id=#{shopEntityId}
AND category_id=#{categoryId}
AND category_name like CONCAT('%',#{categoryName},'%')
GROUP BY category_id,category_name
limit
${pageIndex},
${pageSize}

SELECT COUNT(gc.id) from (SELECT id, category_id, category_name, shop_id, shop_entity_id, parent_category_id, pic_url, sort, position_id, is_del, create_time, c_time_stamp FROM goods_category WHERE is_del='N' AND shop_id=#{shopId}
AND shop_entity_id=#{shopEntityId}
AND category_id=#{categoryId}
AND category_name like CONCAT('%',#{categoryName},'%')
GROUP BY category_id,category_name)as gc;
